/**
 * Determine if the given value is undefined.
 *
 * @param {mixed} value
 * @returns {boolean}
 */
export declare const isUndefined: (value: unknown) => value is undefined;
/**
 * Determine if the given value is null.
 *
 * @param {mixed} value
 * @returns {boolean}
 */
export declare const isNull: (value: unknown) => value is null;
/**
 * Determine if the given value is null or undefined.
 *
 * @param {mixed} value
 * @returns {boolean}
 */
export declare const isNullOrUndefined: (value: unknown) => value is null | undefined;
